import React, { useState, useEffect } from "react";
import "./Quiz.css";

export function Quiz(props) {
    const questions = props.quizData;

    const [showResults, setShowResults] = useState(false);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [score, setScore] = useState(0);
    const [selectedOption, setSelectedOption] = useState(null);
    const [showCorrectAnswer, setShowCorrectAnswer] = useState(false);
    const [fadeIn, setFadeIn] = useState(true);

    const optionClicked = (optionId, isCorrect) => {
        setSelectedOption(optionId);
        setShowCorrectAnswer(true);

        if (isCorrect) {
            setScore(score + 1);
        }

        setTimeout(() => {
            setFadeIn(false);
            setTimeout(() => {
                if (currentQuestion + 1 < questions.length) {
                    setCurrentQuestion(currentQuestion + 1);
                    setSelectedOption(null);
                    setShowCorrectAnswer(false);
                    setFadeIn(true);
                } else {
                    setShowResults(true);
                }
            }, 300);
        }, 1500);
    };

    const restartGame = () => {
        setScore(0);
        setCurrentQuestion(0);
        setShowResults(false);
        setSelectedOption(null);
        setShowCorrectAnswer(false);
        setFadeIn(true);
    };

    const getProgressPercentage = () => {
        return ((currentQuestion + 1) / questions.length) * 100;
    };

    const getScoreMessage = () => {
        const percentage = Math.round((score / questions.length) * 100);
        if (percentage >= 90) return "Excellent! You're a true Hawaiian culture expert! ðŸŒº";
        if (percentage >= 75) return "Great job! You have a solid understanding of Hawaiian culture! ðŸŒŠ";
        if (percentage >= 60) return "Good work! You're learning about Hawaiian culture! ðŸï¸";
        if (percentage >= 40) return "Keep learning! There's always more to discover about Hawai'i! ðŸŒº";
        return "Don't worry! This is a great opportunity to learn more about Hawaiian culture! ðŸŒŠ";
    };

    const getScoreColor = () => {
        const percentage = Math.round((score / questions.length) * 100);
        if (percentage >= 90) return "#4CAF50";
        if (percentage >= 75) return "#8BC34A";
        if (percentage >= 60) return "#FFC107";
        if (percentage >= 40) return "#FF9800";
        return "#F44336";
    };

    return (
        <div className="quiz-container">
            <div className="quiz-background">
                <div className="quiz-overlay"></div>
            </div>

            <div className={`quiz-content ${!showResults && fadeIn ? 'fade-in' : ''}`}>
                {showResults ? (
                    <div className={`quiz-results ${fadeIn ? 'fade-in' : ''}`}>
                        <div className="results-header">
                            <h1 className="results-title">Mahalo for Taking Our Quiz!</h1>
                            <div className="results-subtitle">How well do you know Hawai'i?</div>
                        </div>

                        <div className="score-display">
                            <div className="score-circle" style={{ borderColor: getScoreColor() }}>
                                <div className="score-number" style={{ color: getScoreColor() }}>
                                    {Math.round((score / questions.length) * 100)}%
                                </div>
                                <div className="score-text">
                                    {score} out of {questions.length} correct
                                </div>
                            </div>
                            <div className="score-message" style={{ color: getScoreColor() }}>
                                {getScoreMessage()}
                            </div>
                        </div>

                        <div className="educational-content">
                            <h3 className="education-title">Understanding Hawai'i's History</h3>
                            <div className="education-text">
                                <p>
                                    When Hawai'i was illegally overthrown by the United States in January of 1893, 
                                    over 1.2 million acres of native land was seized and their monarchy was annexed. 
                                    The Hawaiian language was banned from being taught in schools, and the colonization 
                                    of Hawai'i stripped the native population of their culture, traditions, and rightful land.
                                </p>
                                <p>
                                    Today, only 20% of Hawai'i's population is made up by native Hawaiians, and they 
                                    are still fighting to reestablish their governance and spread their traditional customs. 
                                    In the early 1970s, a renaissance of the Hawaiian culture began to promote native 
                                    traditions in language, art, dance, and cuisine.
                                </p>
                                <p>
                                    Despite the positive impact of this renaissance, the tourism business dominates 
                                    the Hawaiian economy and people visiting and moving from the mainland has a negative 
                                    impact on efforts against colonization. Due to this, it is crucial for visitors 
                                    (like you!) to educate themselves on the history and culture of Hawai'i, and plan 
                                    their trips around supporting small and locally owned businesses whose mission aligns 
                                    with that of the native population.
                                </p>
                            </div>
                        </div>

                        <div className="quiz-actions">
                            <button className="restart-button" onClick={restartGame}>
                                <span className="button-icon">ðŸ”„</span>
                                Take Quiz Again
                            </button>
                        </div>
                    </div>
                ) : (
                    <>
                        <div className="quiz-header">
                            <h1 className="quiz-title">How Well Do You Know Hawai'i?</h1>
                            <div className="quiz-subtitle">Test your knowledge of Hawaiian culture, history, and traditions</div>
                        </div>

                        <div className="progress-section">
                            <div className="progress-info">
                                <span className="question-counter">Question {currentQuestion + 1} of {questions.length}</span>
                                <span className="score-counter">Score: {score}</span>
                            </div>
                            <div className="progress-bar">
                                <div 
                                    className="progress-fill" 
                                    style={{ width: `${getProgressPercentage()}%` }}
                                ></div>
                            </div>
                        </div>

                        <div className="question-card">
                            <h2 className="question-text">{questions[currentQuestion].text}</h2>

                            <div className="options-container">
                                {questions[currentQuestion].options.map((option) => {
                                    const isSelected = selectedOption === option.id;
                                    const isCorrect = option.isCorrect;
                                    let optionClass = "option-button";

                                    if (showCorrectAnswer) {
                                        if (isCorrect) {
                                            optionClass += " correct";
                                        } else if (isSelected) {
                                            optionClass += " incorrect";
                                        }
                                    }

                                    return (
                                        <button
                                            key={option.id}
                                            className={optionClass}
                                            onClick={() => !showCorrectAnswer && optionClicked(option.id, option.isCorrect)}
                                            disabled={showCorrectAnswer}
                                        >
                                            <span className="option-text">{option.text}</span>
                                            {showCorrectAnswer && isCorrect && (
                                                <span className="correct-icon">âœ“</span>
                                            )}
                                            {showCorrectAnswer && isSelected && !isCorrect && (
                                                <span className="incorrect-icon">âœ—</span>
                                            )}
                                        </button>
                                    );
                                })}
                            </div>

                            {showCorrectAnswer && (
                                <div className="feedback-message">
                                    {selectedOption === questions[currentQuestion].options.find(opt => opt.isCorrect)?.id ? (
                                        <div className="correct-feedback">
                                            <span className="feedback-icon">ðŸŽ‰</span>
                                            <span>Correct! Great job!</span>
                                        </div>
                                    ) : (
                                        <div className="incorrect-feedback">
                                            <span className="feedback-icon">ðŸ’¡</span>
                                            <span>Keep learning! The correct answer will be highlighted.</span>
                                        </div>
                                    )}
                                </div>
                            )}
                        </div>
                    </>
                )}
            </div>
        </div>
    );
}
