import React, { useState, useEffect } from 'react';
import { ModernHotelCard } from './ModernHotelCard';

export function HotelsPage(props) {
  const [filteredHotels, setFilteredHotels] = useState([]);
  const [filters, setFilters] = useState({
    search: '',
    island: 'all',
    price: 'all',
    amenities: []
  });

  // Filter hotels data
  useEffect(() => {
    let hotels = props.cardData.filter((cardObj) => cardObj.category === "hotel");

    // Apply search filter
    if (filters.search) {
      hotels = hotels.filter(hotel => 
        hotel.title.toLowerCase().includes(filters.search.toLowerCase()) ||
        hotel.description.toLowerCase().includes(filters.search.toLowerCase()) ||
        hotel.island.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Apply island filter
    if (filters.island !== 'all') {
      hotels = hotels.filter(hotel => hotel.island === filters.island);
    }

    // Apply price filter
    if (filters.price !== 'all') {
      hotels = hotels.filter(hotel => hotel.price === filters.price);
    }

    // Apply amenities filter
    if (filters.amenities.length > 0) {
      hotels = hotels.filter(hotel => 
        filters.amenities.some(amenity => hotel.tags.includes(amenity))
      );
    }

    setFilteredHotels(hotels);
  }, [props.cardData, filters]);

  const handleFilterChange = (newFilters) => {
    setFilters(prev => ({ ...prev, ...newFilters }));
  };

  const availableIslands = [...new Set(props.cardData
    .filter(card => card.category === "hotel")
    .map(hotel => hotel.island))];

  const availableAmenities = [...new Set(props.cardData
    .filter(card => card.category === "hotel")
    .flatMap(hotel => hotel.tags))];

  return (
    <div className="hotels-page">
      <div className="hotels-container">
        {/* Sidebar Filters */}
        <div className="hotels-sidebar">
          <div className="sidebar-header">
            <h2>Filters</h2>
            <button 
              className="clear-filters-btn"
              onClick={() => setFilters({
                search: '',
                island: 'all',
                price: 'all',
                amenities: []
              })}
            >
              Clear All
            </button>
          </div>

          {/* Search */}
          <div className="filter-section">
            <h3>Search</h3>
            <input
              type="text"
              placeholder="Search hotels..."
              value={filters.search}
              onChange={(e) => handleFilterChange({ search: e.target.value })}
              className="search-input"
            />
          </div>

          {/* Island Filter */}
          <div className="filter-section">
            <h3>Island</h3>
            <div className="filter-options">
              <label className="filter-option">
                <input
                  type="radio"
                  name="island"
                  value="all"
                  checked={filters.island === 'all'}
                  onChange={(e) => handleFilterChange({ island: e.target.value })}
                />
                <span>All Islands</span>
              </label>
              {availableIslands.map(island => (
                <label key={island} className="filter-option">
                  <input
                    type="radio"
                    name="island"
                    value={island}
                    checked={filters.island === island}
                    onChange={(e) => handleFilterChange({ island: e.target.value })}
                  />
                  <span>{island}</span>
                </label>
              ))}
            </div>
          </div>

          {/* Price Filter */}
          <div className="filter-section">
            <h3>Price Range</h3>
            <div className="filter-options">
              <label className="filter-option">
                <input
                  type="radio"
                  name="price"
                  value="all"
                  checked={filters.price === 'all'}
                  onChange={(e) => handleFilterChange({ price: e.target.value })}
                />
                <span>All Prices</span>
              </label>
              <label className="filter-option">
                <input
                  type="radio"
                  name="price"
                  value="$"
                  checked={filters.price === '$'}
                  onChange={(e) => handleFilterChange({ price: e.target.value })}
                />
                <span>$ Budget</span>
              </label>
              <label className="filter-option">
                <input
                  type="radio"
                  name="price"
                  value="$$"
                  checked={filters.price === '$$'}
                  onChange={(e) => handleFilterChange({ price: e.target.value })}
                />
                <span>$$ Moderate</span>
              </label>
              <label className="filter-option">
                <input
                  type="radio"
                  name="price"
                  value="$$$"
                  checked={filters.price === '$$$'}
                  onChange={(e) => handleFilterChange({ price: e.target.value })}
                />
                <span>$$$ Premium</span>
              </label>
              <label className="filter-option">
                <input
                  type="radio"
                  name="price"
                  value="$$$$"
                  checked={filters.price === '$$$$'}
                  onChange={(e) => handleFilterChange({ price: e.target.value })}
                />
                <span>$$$$ Luxury</span>
              </label>
            </div>
          </div>

          {/* Amenities Filter */}
          <div className="filter-section">
            <h3>Amenities</h3>
            <div className="filter-options">
              {availableAmenities.slice(0, 10).map(amenity => (
                <label key={amenity} className="filter-option">
                  <input
                    type="checkbox"
                    checked={filters.amenities.includes(amenity)}
                    onChange={(e) => {
                      const newAmenities = e.target.checked
                        ? [...filters.amenities, amenity]
                        : filters.amenities.filter(a => a !== amenity);
                      handleFilterChange({ amenities: newAmenities });
                    }}
                  />
                  <span>{amenity}</span>
                </label>
              ))}
            </div>
          </div>
        </div>

        {/* Main Content */}
        <div className="hotels-main">
          <div className="hotels-header">
            <h1>Hotels</h1>
            <p>{filteredHotels.length} hotels found</p>
          </div>

          {filteredHotels.length === 0 ? (
            <div className="no-results">
              <div className="no-results-icon">🏨</div>
              <h3>No hotels found</h3>
              <p>Try adjusting your filters to see more options</p>
            </div>
          ) : (
            <div className="hotels-grid">
              {filteredHotels.map((hotel) => (
                <ModernHotelCard 
                  key={hotel.cardId} 
                  hotel={hotel} 
                />
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
