import React from 'react';

export function CardItems(props) {
  const { title, rating, description, cardImg, tags, island, price } = props.cardData;

  const tagsArray = tags.map((item) => {
    const element = (
      <Tag tag={item} key={item}/>
    )
    return element;
  })

  // Generate star rating
  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<span key={i} className="star filled">‚òÖ</span>);
    }

    if (hasHalfStar) {
      stars.push(<span key="half" className="star half">‚òÖ</span>);
    }

    const emptyStars = 5 - Math.ceil(rating);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(<span key={`empty-${i}`} className="star empty">‚òÜ</span>);
    }

    return stars;
  };

  return (
    <div className="card__box col-lg-6 col-md-12">
      <div className="enhanced-card">
        <div className="card__img">
          <img 
            className="card__img-preview" 
            src={cardImg} 
            alt={title}
            onError={(e) => {
              e.target.src = 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80';
            }}
          />
          <div className="card__overlay">
            <div className="card__price-badge">{price}</div>
            <div className="card__island-badge">{island}</div>
          </div>
        </div>
        <div className="card__content">
          <div className="card__header">
            <h4 className="card__title">
              <a href="#">{title}</a>
            </h4>
            <div className="card__rating">
              {renderStars(rating)}
              <span className="rating-text">({rating})</span>
            </div>
          </div>
          <p className="card__description">{description}</p>
          <div className="card__tags">
            {tagsArray.slice(0, 3)} {/* Show only first 3 tags */}
            {tags.length > 3 && (
              <span className="more-tags">+{tags.length - 3} more</span>
            )}
          </div>
          <div className="card__footer">
            <div className="card__location">
              <span className="location-icon">üìç</span>
              <span>{island}</span>
            </div>
            <div className="card__price">
              <span className="price-label">From</span>
              <span className="price-value">{price}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

function Tag(props) {
  const tag = props.tag;
  return (
    <span className="enhanced-tag">{tag}</span>
  )
}
