import React, { useState } from "react";

export function Quiz(props) {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [isTransitioning, setIsTransitioning] = useState(false);

  // Preference-based questions for personalized recommendations
  const questions = [
    {
      id: 1,
      question: "What type of traveler are you?",
      options: [
        { id: "adventure", text: "Adventure Seeker", icon: "🏔️", description: "I love exciting activities and new challenges" },
        { id: "relaxation", text: "Relaxation Lover", icon: "🌺", description: "I prefer peaceful, calming experiences" },
        { id: "culture", text: "Cultural Explorer", icon: "🏺", description: "I want to learn about Hawaiian history and traditions" },
        { id: "luxury", text: "Luxury Enthusiast", icon: "✨", description: "I enjoy premium experiences and high-end amenities" }
      ]
    },
    {
      id: 2,
      question: "Which island interests you most?",
      options: [
        { id: "oahu", text: "Oahu", icon: "🏙️", description: "The Gathering Place - urban and diverse" },
        { id: "maui", text: "Maui", icon: "🌋", description: "The Valley Isle - beaches and mountains" },
        { id: "kauai", text: "Kauai", icon: "🌿", description: "The Garden Isle - lush and natural" },
        { id: "hawaii", text: "Hawaii Island", icon: "🔥", description: "The Big Island - volcanoes and adventure" }
      ]
    },
    {
      id: 3,
      question: "What activities interest you most?",
      options: [
        { id: "water", text: "Water Activities", icon: "🏄‍♂️", description: "Surfing, snorkeling, sailing, swimming" },
        { id: "land", text: "Land Adventures", icon: "🥾", description: "Hiking, biking, exploring nature" },
        { id: "culture", text: "Cultural Experiences", icon: "🌺", description: "Luaus, historical tours, traditional crafts" },
        { id: "food", text: "Culinary Experiences", icon: "🍽️", description: "Local cuisine, cooking classes, food tours" }
      ]
    },
    {
      id: 4,
      question: "What's your accommodation preference?",
      options: [
        { id: "boutique", text: "Boutique Hotels", icon: "🏨", description: "Unique, locally-owned properties" },
        { id: "resort", text: "Resorts", icon: "🏖️", description: "Full-service with amenities" },
        { id: "inn", text: "Local Inns", icon: "🏡", description: "Charming, intimate accommodations" },
        { id: "budget", text: "Budget-Friendly", icon: "💰", description: "Affordable local options" }
      ]
    },
    {
      id: 5,
      question: "What's your travel budget?",
      options: [
        { id: "budget", text: "Budget Conscious", icon: "💵", description: "Looking for great value" },
        { id: "moderate", text: "Moderate", icon: "💳", description: "Balanced quality and price" },
        { id: "premium", text: "Premium", icon: "💎", description: "Quality experiences worth the cost" },
        { id: "luxury", text: "Luxury", icon: "👑", description: "The finest experiences available" }
      ]
    }
  ];

  const handleAnswer = (answerId) => {
    setIsTransitioning(true);

    // Save the answer
    setAnswers(prev => ({
      ...prev,
      [currentQuestion]: answerId
    }));

    // Move to next question or show results
    setTimeout(() => {
      if (currentQuestion + 1 < questions.length) {
        setCurrentQuestion(currentQuestion + 1);
      } else {
        setShowResults(true);
      }
      setIsTransitioning(false);
    }, 300);
  };

  const goBack = () => {
    if (currentQuestion > 0) {
      setIsTransitioning(true);
      setTimeout(() => {
        setCurrentQuestion(currentQuestion - 1);
        setIsTransitioning(false);
      }, 300);
    }
  };

  const restartQuiz = () => {
    setCurrentQuestion(0);
    setAnswers({});
    setShowResults(false);
  };

  const getRecommendations = () => {
    const recommendations = {
      hotels: [],
      dining: [],
      activities: []
    };

    // Simple recommendation logic based on answers
    if (answers[0] === "luxury") {
      recommendations.hotels.push("Luxury resorts and boutique hotels");
    } else if (answers[0] === "adventure") {
      recommendations.activities.push("Hiking tours and adventure activities");
    } else if (answers[0] === "culture") {
      recommendations.activities.push("Cultural experiences and luaus");
    }

    if (answers[1] === "oahu") {
      recommendations.hotels.push("Urban accommodations in Honolulu");
      recommendations.dining.push("Diverse dining options in Waikiki");
    } else if (answers[1] === "maui") {
      recommendations.activities.push("Beach activities and whale watching");
    }

    if (answers[2] === "water") {
      recommendations.activities.push("Water sports and ocean activities");
    } else if (answers[2] === "food") {
      recommendations.dining.push("Local Hawaiian cuisine and food tours");
    }

    return recommendations;
  };

  const progressPercentage = ((currentQuestion + 1) / questions.length) * 100;

  if (showResults) {
    const recommendations = getRecommendations();

    return (
      <div className="quiz-page">
        <div className="quiz-container">
          <div className="results-section">
            <div className="results-header">
              <h1>🌺 Your Hawaiian Experience Profile</h1>
              <p>Based on your preferences, here are our recommendations for your perfect Hawaiian adventure!</p>
            </div>

            <div className="recommendations-grid">
              <div className="recommendation-card">
                <div className="recommendation-icon">🏨</div>
                <h3>Accommodations</h3>
                <ul>
                  <li>Local Hawaiian-owned hotels and inns</li>
                  <li>Authentic island hospitality</li>
                  <li>Cultural immersion experiences</li>
                </ul>
              </div>

              <div className="recommendation-card">
                <div className="recommendation-icon">🍽️</div>
                <h3>Dining</h3>
                <ul>
                  <li>Traditional Hawaiian cuisine</li>
                  <li>Farm-to-table local restaurants</li>
                  <li>Authentic cultural dining experiences</li>
                </ul>
              </div>

              <div className="recommendation-card">
                <div className="recommendation-icon">🏄‍♂️</div>
                <h3>Activities</h3>
                <ul>
                  <li>Cultural luaus and traditional experiences</li>
                  <li>Local-guided tours and adventures</li>
                  <li>Authentic Hawaiian cultural activities</li>
                </ul>
              </div>
            </div>

            <div className="results-cta">
              <h3>Ready to Explore?</h3>
              <p>Discover authentic local businesses that match your preferences</p>
              <div className="cta-buttons">
                <button className="btn-primary" onClick={() => window.location.href = '/hotels'}>
                  Browse Hotels
                </button>
                <button className="btn-secondary" onClick={() => window.location.href = '/dinings'}>
                  Explore Dining
                </button>
                <button className="btn-secondary" onClick={() => window.location.href = '/activities'}>
                  Find Activities
                </button>
              </div>
            </div>

            <button className="restart-btn" onClick={restartQuiz}>
              Take Quiz Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="quiz-page">
      <div className="quiz-container">
        {/* Progress Bar */}
        <div className="progress-section">
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${progressPercentage}%` }}
            ></div>
          </div>
          <p className="progress-text">
            Question {currentQuestion + 1} of {questions.length}
          </p>
        </div>

        {/* Question Card */}
        <div className={`question-card ${isTransitioning ? 'transitioning' : ''}`}>
          <h2 className="question-title">{questions[currentQuestion].question}</h2>

          <div className="options-grid">
            {questions[currentQuestion].options.map((option) => (
              <button
                key={option.id}
                className="option-button"
                onClick={() => handleAnswer(option.id)}
              >
                <div className="option-icon">{option.icon}</div>
                <div className="option-content">
                  <h3>{option.text}</h3>
                  <p>{option.description}</p>
                </div>
              </button>
            ))}
          </div>

          {/* Navigation */}
          <div className="quiz-navigation">
            {currentQuestion > 0 && (
              <button className="back-btn" onClick={goBack}>
                ← Back
              </button>
            )}
            <div className="question-counter">
              {currentQuestion + 1} / {questions.length}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
